import { SET_TOURNAMENTS, LOADING_DATA} from '../types';

const initialState = {
    tournaments: [],
    tournament: {},
    loading: false
};

export default function(state = initialState, actions){
    switch(actions.type){
        case LOADING_DATA:
            return {
                ...state,
                loading: true
            }
        case SET_TOURNAMENTS:
            return {
                ...state,
                tournaments: action.payload,
                loading: false
            }
    }
}
