import React, { Component } from 'react'
import PropTypes from 'prop-types';
import axios from 'axios';
import TournamentDetails from '../components/tournament/TournamentDetails';
import TournamentMatches from '../components/tournament/TournamentMatches';
import StaticProfile from '../components/profile/StaticProfile';
import withStyles from '@material-ui/core/styles/withStyles';
import { Link } from 'react-router-dom';
import TextField from '@material-ui/core/TextField';
import Button from '@material-ui/core/Button';



import { connect } from 'react-redux';
import { postTournament, clearErrors } from '../redux/actions/dataActions';

// MUI stuffs
import Card from '@material-ui/core/Card';
import CardContent from '@material-ui/core/CardContent';
import CardMedia from '@material-ui/core/CardMedia';
import Typography from '@material-ui/core/Typography';
import Grid from '@material-ui/core/Grid';
import MuiLink from '@material-ui/core/Link';

import {
    MuiPickersUtilsProvider,
    KeyboardTimePicker,
    KeyboardDatePicker,
  } from '@material-ui/pickers';
import DateFnsUtils from '@date-io/date-fns';


import InputLabel from '@material-ui/core/InputLabel';
import MenuItem from '@material-ui/core/MenuItem';
import FormHelperText from '@material-ui/core/FormHelperText';
import FormControl from '@material-ui/core/FormControl';
import Select from '@material-ui/core/Select';

const styles = {
    image: {
        width: 120,
        objectFit: 'cover',
        maxWidth: '100%',
        borderRadius: '50%'
    },
    typography: {
        flexGrow: 1,
        align: "center"
      },
    title: {
        paddingBottom: "20px"
    },

}

class createTournament extends Component {

    state = {
        name: '',
        teamsN: '',
        date: '',
        errors: {}
    };

    componentWillReceiveProps(nextProps){
        if(nextProps.UI.errors){
            this.setState({
                errors: nextProps.UI.errors
            });
        }
        if (!nextProps.UI.errors && !nextProps.UI.loading) {
            this.setState({ name: '', teamsN: '', date: '', errors: {} });
        }
    
    }

    handleChange = (event) => {
        this.props.clearErrors();
        this.setState({ [event.target.name]: event.target.value })
    };
    handleSubmit = (event) => {
        event.preventDefault();
        this.props.postTournament({ name: this.state.name });
    };

    handleDateChange = (date) => {
        this.date = date;
    };

    render() {

        const { errors } = this.state;
        const { 
            classes, 
            user: { 
                authenticated
            }
        } = this.props;

        if(!authenticated){
            console.log("AUTH: " + authenticated)
        }

        const authText = (
            !authenticated ? (
                <Grid container  justify="center">
                    <Grid item  xs='auto'>
                        <Card >
                            <CardContent>
                                <Typography variant="body1" className={classes.typography}>You have to&nbsp;
                                    <MuiLink component={Link} to={`/login`} variant="body1">
                                         login 
                                    </MuiLink> 
                                    &nbsp;to create a Tournament!
                                </Typography>
                            </CardContent>
                        </Card>
                    </Grid>
                </Grid>
            ) : (
                <Grid container direction="row">
                    <Grid item sm={12} xs={12}>
                        <form onSubmit={this.handleSubmit}>
                            <Grid container direction="row">
                                <Grid item sm={6} xs={12}>
                                    <Card>
                                        <CardContent>
                                            <TextField 
                                                name="name" 
                                                type="text" 
                                                label="Tournament name" 
                                                multiline 
                                                rows="3" 
                                                placeholder="tournament name" 
                                                error={errors.name ? true : false} 
                                                helperText={errors.name} 
                                                className={classes.textField}
                                                onChange={this.handleChange}
                                                fullWidth
                                            />
                                            <TextField 
                                                name="location" 
                                                type="text" 
                                                label="Location" 
                                                multiline 
                                                rows="3" 
                                                placeholder="tournament location" 
                                                error={errors.name ? true : false} 
                                                helperText={errors.name} 
                                                className={classes.textField}
                                                onChange={this.handleChange}
                                                fullWidth
                                            />

                                            <InputLabel id="demo-simple-select-label">Format</InputLabel>
                                            <Select
                                                labelId="demo-simple-select-label"
                                                id="demo-simple-select"
                                                value={this.teamsN}
                                                onChange={this.handleChange}
                                                >
                                                <MenuItem value={"Single Elimination"}>Single Elimination</MenuItem>
                                                <MenuItem disabled={true} value={"Double Elimination"}>Double Eliminaton</MenuItem>
                                                <MenuItem disabled={true} value={"Straight Round Robin"}>Straight Round Robin</MenuItem>
                                            </Select>

                                            <TextField 
                                                name="description" 
                                                type="text" 
                                                label="Description" 
                                                multiline 
                                                rows="5" 
                                                placeholder="tournament description" 
                                                error={errors.name ? true : false} 
                                                helperText={errors.name} 
                                                className={classes.textField}
                                                onChange={this.handleChange}
                                                fullWidth
                                            />
                                            <MuiPickersUtilsProvider utils={DateFnsUtils}>
                                                <KeyboardDatePicker
                                                    disableToolbar
                                                    variant="inline"
                                                    format="dd/mm/yyyy"
                                                    margin="normal"
                                                    id="date-picker-inline"
                                                    label="Date picker inline"
                                                    value={this.date}
                                                    onChange={this.handleDateChange}
                                                    KeyboardButtonProps={{
                                                        'aria-label': 'change date',
                                                    }}
                                                />
                                            </MuiPickersUtilsProvider>

                                            
                                        </CardContent>
                                    </Card>
                                </Grid>

                                <Grid item sm={6} xs={12}>
                                    <Card>
                                        <CardContent>
                                            
                                            <InputLabel id="demo-simple-select-label">Teams #</InputLabel>
                                            <Select
                                                labelId="demo-simple-select-label"
                                                id="demo-simple-select"
                                                value={this.teamsN}
                                                onChange={this.handleChange}
                                                >
                                                <MenuItem value={4}>4</MenuItem>
                                                <MenuItem value={8}>8</MenuItem>
                                                <MenuItem value={16}>16</MenuItem>
                                            </Select>

                                            <InputLabel id="demo-simple-select-label">Type</InputLabel>
                                            <Select
                                                labelId="demo-simple-select-label"
                                                id="demo-simple-select"
                                                value={this.teamsN}
                                                onChange={this.handleChange}
                                                >
                                                <MenuItem value={"5-A-Side"}>5-A-Side</MenuItem>
                                                <MenuItem value={"6-A-Side"}>7-A-Side</MenuItem>
                                                <MenuItem value={"7-A-Side"}>6-A-Side</MenuItem>
                                                <MenuItem value={"8-A-Side"}>8-A-Side</MenuItem>
                                                <MenuItem value={"9-A-Side"}>9-A-Side</MenuItem>
                                                <MenuItem value={"11-A-Side"}>11-A-Side</MenuItem>
                                            </Select>

                                            <TextField 
                                                name="date" 
                                                type="text" 
                                                label="Date" 
                                                multiline 
                                                rows="3" 
                                                placeholder="tournament date" 
                                                error={errors.name ? true : false} 
                                                helperText={errors.name} 
                                                className={classes.textField}
                                                onChange={this.handleChange}
                                                fullWidth
                                            />
                                            
                                        </CardContent>
                                    </Card>
                                </Grid>
                            </Grid>
                            <Button type="submit" variant="contained" color="primary"
                                className={classes.submitButton}>
                                    Submit
                            </Button>
                        </form>
                    </Grid>
                </Grid>
            )
        );
        

        return (

            <Grid container spacing={0} direction="row" alignItems="center">
                <Grid container className={classes.title}>
                    <Grid item sm={10} xs={12}>
                        <Typography variant="h5" color="primary" className={classes.typography}><b>Create Tournament</b></Typography>
                    </Grid>
                </Grid>
                {authText}
            </Grid>
        )
    }
}

createTournament.protoTypes = {
    classes: PropTypes.object.isRequired,

    user: PropTypes.object.isRequired,

    postTournament: PropTypes.func.isRequired,
    clearErrors: PropTypes.func.isRequired,
    UI: PropTypes.object.isRequired
}

const mapStateToProps = state => ({
    user: state.user,

    UI: state.UI
})

export default connect(mapStateToProps, {postTournament, clearErrors})(withStyles(styles)(createTournament));
