import React, { Component } from 'react'
import PropTypes from 'prop-types';
import axios from 'axios';
import TournamentDetails from '../components/tournament/TournamentDetails';
import TournamentMatches from '../components/tournament/TournamentMatches';
import StaticProfile from '../components/profile/StaticProfile';
import withStyles from '@material-ui/core/styles/withStyles';



import { connect } from 'react-redux';
import { postTournament, clearErrors } from '../redux/actions/dataActions';

// MUI stuffs
import Card from '@material-ui/core/Card';
import CardContent from '@material-ui/core/CardContent';
import CardMedia from '@material-ui/core/CardMedia';
import Typography from '@material-ui/core/Typography';
import Grid from '@material-ui/core/Grid';

const styles = {
    image: {
        width: 120,
        objectFit: 'cover',
        maxWidth: '100%',
        borderRadius: '50%'
    },
    typography: {
        flexGrow: 1,
        align: "center"
      },
    title: {
        paddingBottom: "20px"
    }

}

class createTournament extends Component {
    state = {
        profile: null
    }

    componentDidMount(){
        //const tournamentId = this.props.match.params.tournamentId;
        //console.log("TEST " + tournamentId);
        //this.props.getTournament(tournamentId);
        // axios.get(`/tournament/${tournamentId}`)
        //      .then(res => {
        //          this.setState({
        //              profile: res.data.user
        //          })
        //      })
        //     .catch(err => console.log(err));
    }

    render() {


        const { 
            classes, 
            user: { 
                credentials: {handle, createdAt, imageUrl}, 
                loading,
                authenticated
            }
        } = this.props;

        if(authenticated){
            console.log("AUTH : " + authenticated);
        }
        

        return (

            <Grid container spacing={0} direction="row" alignItems="center">
                <Grid container className={classes.title}>
                    <Grid item sm={10} xs={12}>
                        <Typography variant="h5" color="primary" className={classes.typography}><b>Create Tournament</b></Typography>
                    </Grid>
                </Grid>
            </Grid>
        )
    }
}

createTournament.protoTypes = {
    data: PropTypes.object.isRequired,
    classes: PropTypes.object.isRequired,

    user: PropTypes.object.isRequired,

    postTournament: PropTypes.func.isRequired,
    clearErrors: PropTypes.func.isRequired,
    UI: PropTypes.object.isRequired
}

const mapStateToProps = state => ({
    data: state.data,

    UI: state.UI
})

export default connect(mapStateToProps, {postTournament, clearErrors})(withStyles(styles)(createTournament));
