import React, { Component } from 'react'
import withStyles from '@material-ui/core/styles/withStyles';
import PropTypes from 'prop-types';
import { Link } from 'react-router-dom';
import dayjs from 'dayjs';
import relativeTime from 'dayjs/plugin/relativeTime';
import MyButton from '../../utility/MyButton';
import DeleteTournament from './DeleteTournament';
import TournamentDialog from './TournamentDialog';

// MUI stuffs
import Card from '@material-ui/core/Card';
import CardContent from '@material-ui/core/CardContent';
import CardMedia from '@material-ui/core/CardMedia';
import Typography from '@material-ui/core/Typography';


import { connect } from 'react-redux' ;
import { deleteTournament } from '../../redux/actions/dataActions';
import { CardActions } from '@material-ui/core';


const styles = {
    card: {
        position: 'relative',
        display: 'flex',
        marginBottom: 20
    },
    image: {
        minWidth: 200,
        objectFit: 'cover'
    },
    content: {
        padding: 25
    }
}

class TournamentDetails extends Component {
    render() {
        dayjs.extend(relativeTime);
        const { classes,
             tournament: { 
                 name, createdAt, userImage, userHandle, tournamentId, commentCount
                },
            user: { 
                authenticated, credentials: { handle }
            } 
        } = this.props;

        // fa check se' l'user giusto
        const deleteButton = authenticated && userHandle === handle ? (
            <DeleteTournament tournamentId={tournamentId}/>
        ) : null
        
        return (
            <Card className={classes.card}>
                <CardContent className={classes.content}>
                    <Typography variant="h6" color="primary">
                        Teams #:
                    </Typography>
                    <Typography variant="h6" color="primary" >
                        Type:
                    </Typography>
                    <Typography variant="h6" color="primary">
                        Format:
                    </Typography>
                    <Typography variant="h6" color="primary">
                        Location:
                    </Typography>
                    <Typography variant="h6" color="primary">
                        Start date:
                    </Typography>
                    <Typography variant="h6" color="primary">
                        Description:
                    </Typography>
                </CardContent>

                <CardContent className={classes.content}>
                    <Typography variant="h6" color="primary">
                        Teams #:
                    </Typography>
                    <Typography variant="h6" color="primary" >
                        Type:
                    </Typography>
                    <Typography variant="h6" color="primary">
                        Format:
                    </Typography>
                    <Typography variant="h6" color="primary">
                        Location:
                    </Typography>
                    <Typography variant="h6" color="primary">
                        Start date:
                    </Typography>
                    <Typography variant="h6" color="primary">
                        Description:
                    </Typography>
                </CardContent>
            </Card>

            
        )
    }
}

TournamentDetails.propTypes = {
    user: PropTypes.object.isRequired,
    tournament: PropTypes.object.isRequired,
    classes: PropTypes.object.isRequired
}

const mapStateToProps = state => ({
    user: state.user
})

const mapActionsToProps = {
    
}

export default connect(mapStateToProps, mapActionsToProps)(withStyles(styles)(TournamentDetails));
