import React, { Component } from 'react'
import withStyles from '@material-ui/core/styles/withStyles';
import PropTypes from 'prop-types';
import { Link } from 'react-router-dom';
import dayjs from 'dayjs';
import relativeTime from 'dayjs/plugin/relativeTime';
import MyButton from '../../utility/MyButton';
import DeleteTournament from './DeleteTournament';
import TournamentDialog from './TournamentDialog';

// MUI stuffs
import Card from '@material-ui/core/Card';
import CardContent from '@material-ui/core/CardContent';
import CardMedia from '@material-ui/core/CardMedia';
import Typography from '@material-ui/core/Typography';
import { DataGrid } from '@material-ui/data-grid';


import { connect } from 'react-redux' ;
import { deleteTournament } from '../../redux/actions/dataActions';


const styles = {
    
}

const columns = [
    { field: 'tournamentName', headerName: 'Tournament Name', width: 130 },
    { field: 'teamsN', headerName: 'Teams #', type: 'number', width: 90, },
    { field: 'type', headerName: 'Type', width: 130 },
    { field: 'format', headerName: 'Format', width: 130 },
    { field: 'location', headerName: 'Location', width: 130 },
    { field: 'description', headerName: 'Description', width: 130 },
    { field: 'date', headerName: 'Start Date', width: 130 },

    // {
    //   field: 'fullName',
    //   headerName: 'Full name',
    //   description: 'This column has a value getter and is not sortable.',
    //   sortable: false,
    //   width: 160,
    //   valueGetter: (params) =>
    //     `${params.getValue('firstName') || ''} ${params.getValue('lastName') || ''}`,
    // },
];
  
const rows = [
    { id: 1, lastName: 'Snow', firstName: 'Jon', age: 35 },
    { id: 2, lastName: 'Lannister', firstName: 'Cersei', age: 42 },
    { id: 3, lastName: 'Lannister', firstName: 'Jaime', age: 45 },
    { id: 4, lastName: 'Stark', firstName: 'Arya', age: 16 },
    { id: 5, lastName: 'Targaryen', firstName: 'Daenerys', age: null },
    { id: 6, lastName: 'Melisandre', firstName: null, age: 150 },
    { id: 7, lastName: 'Clifford', firstName: 'Ferrara', age: 44 },
    { id: 8, lastName: 'Frances', firstName: 'Rossini', age: 36 },
    { id: 9, lastName: 'Roxie', firstName: 'Harvey', age: 65 },
];

class TournamentTable extends Component {


    render() {
        dayjs.extend(relativeTime);
        console.log("TEST2: " + this.props.tournaments);
        
        return (
            <div style={{ height: 400, width: '100%' }}>
              {/* <DataGrid rows={rows} columns={columns} pageSize={5} checkboxSelection /> */}
            </div>
          );
    }
}

TournamentTable.propTypes = {
    user: PropTypes.object.isRequired,
    tournament: PropTypes.object.isRequired,
    classes: PropTypes.object.isRequired
}

const mapStateToProps = state => ({
    user: state.user
})

const mapActionsToProps = {
    
}

export default connect(mapStateToProps, mapActionsToProps)(withStyles(styles)(TournamentTable));
