import React, { Component } from 'react'
import withStyles from '@material-ui/core/styles/withStyles';
import PropTypes from 'prop-types';
import { Link } from 'react-router-dom';
import dayjs from 'dayjs';
import relativeTime from 'dayjs/plugin/relativeTime';
import MyButton from '../../utility/MyButton';
import DeleteTournament from './DeleteTournament';
import TournamentDialog from './TournamentDialog';
import StaticProfile from '../../components/profile/StaticProfile';
import axios from 'axios';

// MUI stuffs
import Card from '@material-ui/core/Card';
import CardContent from '@material-ui/core/CardContent';
import CardMedia from '@material-ui/core/CardMedia';
import Typography from '@material-ui/core/Typography';
import Grid from '@material-ui/core/Grid';


import { connect } from 'react-redux' ;
import { getUserData } from '../../redux/actions/dataActions';
import { CardActions } from '@material-ui/core';


const styles = {
    card: {
        position: 'relative',
        display: 'flex',
        marginBottom: 20
    },
    image: {
        minWidth: 200,
        objectFit: 'cover'
    },
    content: {
        padding: 25
    }
}

class TournamentDetails extends Component {

    

    constructor() {
        super();
        this.state = {
          profile: null
        };
        this.props.tournament.userHandle = this.props.tournament.userHandle.bind(this);
      }

    
    componentDidMount(){
        const handle2 = this.props.tournament.userHandle;
        console.log("TEST 2: " + this.props.tournament.userHandle)
        this.props.getUserData(handle2);
        axios.get(`/user/${handle2}`)
            .then(res => {
                this.setState({
                    profile: res.data.user
                })
            })
            .catch(err => console.log(err));
    }

    render() {
        
        dayjs.extend(relativeTime);
        const { classes,
             tournament: { 
                 name, createdAt, userImage, userHandle, tournamentId, commentCount, teamsN, type, format, location, description
                },
            user: { 
                authenticated, credentials: { handle }
            } 
        } = this.props;

        // fa check se' l'user giusto
        const deleteButton = authenticated && userHandle === handle ? (
            <DeleteTournament tournamentId={tournamentId}/>
        ) : null
        
        return (
            <Grid container spacing={0} direction="row" alignItems="center">
                <Grid item sm={8} xs={12}>
                    <Card className={classes.card}>
                        <CardContent className={classes.content}>
                            <Typography variant="h6">
                                <b>
                                Teams #:
                                </b>
                            </Typography>
                            <Typography variant="h6">
                                <b>Type:</b>
                            </Typography>
                            <Typography variant="h6">
                                <b>Format:</b>
                            </Typography>
                            <Typography variant="h6">
                                <b>Location:</b>
                            </Typography>
                            <Typography variant="h6">
                                <b>Description:</b>
                            </Typography>
                        </CardContent>

                        <CardContent className={classes.content}>
                            <Typography variant="h6">
                                {name}
                            </Typography>
                            <Typography variant="h6">
                                {type}
                            </Typography>
                            <Typography variant="h6">
                                {format}
                            </Typography>
                            <Typography variant="h6">
                                {location}
                            </Typography>
                            <Typography variant="h6">
                                {description}
                            </Typography>
                        </CardContent>
                     </Card>
                </Grid>
                <Grid item sm={4} xs={12}>
                    {/* <StaticProfile profile={this.state.profile}/> */}
                </Grid>
            </Grid>
            
        )
    }
}

TournamentDetails.propTypes = {
    user: PropTypes.object.isRequired,
    tournament: PropTypes.object.isRequired,
    classes: PropTypes.object.isRequired,
    getUserData: PropTypes.func.isRequired
}

const mapStateToProps = state => ({
    user: state.user
})

const mapActionsToProps = {
    
}

export default connect(mapStateToProps, {getUserData})(withStyles(styles)(TournamentDetails));
