import { SET_TOURNAMENTS, LOADING_DATA } from '../types';
import axios from 'axios';

// get all tournaments
export const getTournaments = () => dispatch => {
    dispatch({LOADING_DATA});
    axios.get('/tournaments')
        .then(res => {
            dispatch({
                type: SET_TOURNAMENTS,
                payload: res.data
            })
        })
        .catch(err => {
            dispatch({
                type: SET_TOURNAMENTS,
                payload: []
            })
        })

}
