import React, { Component } from 'react'
import PropTypes from 'prop-types';
import axios from 'axios';
import Tournament from '../components/tournament/Tournament';
import StaticProfile from '../components/profile/StaticProfile';


import { connect } from 'react-redux';
import { getUserData, getTournament } from '../redux/actions/dataActions';

// MUI stuffs
import Card from '@material-ui/core/Card';
import CardContent from '@material-ui/core/CardContent';
import CardMedia from '@material-ui/core/CardMedia';
import Typography from '@material-ui/core/Typography';
import Grid from '@material-ui/core/Grid';

class tournament extends Component {
    state = {
        profile: null
    }
    componentDidMount(){
        const tournamentId = this.props.match.params.tournamentId;
        console.log("TEST " + tournamentId);
        this.props.getTournament(tournamentId);
        // axios.get(`/tournament/${tournamentId}`)
        //      .then(res => {
        //          this.setState({
        //              profile: res.data.user
        //          })
        //      })
        //     .catch(err => console.log(err));
    }
    render() {
        const { tournament } = this.props.data;

        const { classes } = this.props;

        console.log("TOURN? " + tournament.matches[1].t1);

        <Card className={classes.card}>
            <CardMedia className={classes.image} image={tournament.userImage} title="Profile image"/>
            <CardContent className={classes.content}>
                <Typography variant="h5" color="primary" component={Link} to={`/`}>
                    {tournament.name}
                </Typography>
            </CardContent>
        </Card>

        return null
    }
}

tournament.protoTypes = {
    getUserData: PropTypes.func.isRequired,
    getTournament: PropTypes.func.isRequired,
    data: PropTypes.object.isRequired
}

const mapStateToProps = state => ({
    data: state.data
})

export default connect(mapStateToProps, {getUserData, getTournament})(tournament);
